	my ($loglistmonthmonth, $loglistdayday, $loglistyear) = split (/\//, $listEntry->{'created'});
	
## WHY DO THIS?  WE ARE GETTING THE NON-PADDED VERSION BELOW
	my $loglistmonth = $loglistmonthmonth;
