      	"/archive-$loglistmonthmonth$loglistyearyear\.".$CONFIGS->{'gmlogarchivesuffix'};
		}
	}

	my $loglistmainbody = $gmEntry->{'maintext'};
	my $loglistmorebody = $gmEntry->{'extendedtext'};
	my $loglistsubject = $gmEntry->{'subject'};

###############################################################################
